// Extract sections and initialize variables 
let sections = $input.first().json.output.enhanced_sections || [];
let niche = $('Visual Generation Webhook').first().json.body.niche || 'tech';
let style = $('Visual Generation Webhook').first().json.body.style || 'informative';
let contentId = $('Visual Generation Webhook').first().json.body.contentId || `fallback_${Date.now()}`;
let directories = $('Visual Generation Webhook').first().json.body.directories || {
    main: "/data/shared/fallback/main",
    audio: "/data/shared/fallback/audio",
    images: "/data/shared/fallback/images",
    video: "/data/shared/fallback/video",
    final: "/data/shared/fallback/final"
};

// Create image prompts array for batching
const imagePrompts = sections.map((section, index) => {
    return {
        index,
        section_name: section.section_name,
        prompt: section.original_prompt,
        enhancedPrompt: section.enhanced_prompt
    };
});

// Default image settings optimized for NVIDIA 3050
const defaultImageSettings = {
    width: 768,
    height: 768,
    steps: 30,
    cfgScale: 1,
    sampler: "Euler",
    negativePrompt: "low quality, blurry, distorted, deformed, disfigured, bad anatomy, watermark, signature, out of frame, cropped"
};

return {
    contentId,
    directories,
    niche,
    style,
    imagePrompts,
    imageSettings: defaultImageSettings,
    totalImages: imagePrompts.length,
    completedImages: 0,
    // Initialize with the first image for the first batch
    currentImageIndex: 0
};

// Extract the image data from the response
const responseData = $input.item.json;
let imageData;

// Check if the expected path exists, otherwise look for alternatives
if (responseData.body && responseData.body.images && responseData.body.images.length > 0) {
  imageData = responseData.body.images[0];
} else if (responseData.images && responseData.images.length > 0) {
  imageData = responseData.images[0];
} else {
  // Create a 1x1 transparent pixel as fallback
  imageData = "R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";
  console.log("Using fallback image - no image data found in response");
}

// Get the runIndex directly from the execution or use a reasonable fallback
// This uses the Split In Batches execution index which increases with each image
const runIndex = $runIndex || 0;

// Format the runIndex with leading zeros for sequential naming
const formattedIndex = runIndex.toString().padStart(2, '0');

// Create a sequential filename based on the runIndex
const sequentialFilename = `image${formattedIndex}`;

// Get section information from the input
const index = $input.item.json.index || 0;
const sectionName = $input.item.json.section_name || `section_${index}`;

// Decode base64 image
const imageBuffer = Buffer.from(imageData, 'base64');

// Return with binary data and sequential filename
return [{
  json: {
    ...responseData,
    currentIndex: index,
    sectionIndex: index,
    sectionName: sectionName,
    sequentialFilename: sequentialFilename,
    runIndex: runIndex  // Include this for debugging and downstream nodes
  },
  binary: {
    data: {
      data: imageBuffer,
      mimeType: 'image/png',
      fileName: `${sequentialFilename}.png`
    }
  }
}];

// Create a placeholder image for failed generations
// Get the index and format with leading zeros
const index = $input.item.json.index || 0;
const formattedIndex = index.toString().padStart(2, '0');
const sectionName = $input.item.json.section_name || `section_${index}`;

// Create a 1x1 transparent pixel as a placeholder image
const placeholderBase64 = "R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";
const imageBuffer = Buffer.from(placeholderBase64, 'base64');

// Return with binary data format matching the successful case
return [{
  json: {
    ...$input.item.json,
    currentIndex: index,
    sectionName: sectionName,
    imageGenerationFailed: true
  },
  binary: {
    data: {
      data: imageBuffer,
      mimeType: 'image/png',
      fileName: `placeholder_${formattedIndex}.png`
    }
  }
}];

// Get the current run index from n8n
const runIndex = $runIndex || 0;

// Get total images count
const totalImages = $node["Prepare Image Prompts"].json.totalImages;

// Get current local image count - this will always be 1 per execution 
let completedImages = $input.item.json.completedImages || 0;
completedImages++; // Increment for this execution

// Get current image index
let currentImageIndex = $input.item.json.currentImageIndex || 0;

// Calculate if all images are completed using runIndex
// We need to account for zero-based indexing of runIndex
const allImagesCompleted = (runIndex + 1) >= totalImages;

// Add detailed logging
console.log(`===== DEBUG COMPLETION STATUS =====`);
console.log(`Run Index: ${runIndex}`);
console.log(`Total Images: ${totalImages}`);
console.log(`Completed Images (local): ${completedImages}`);
console.log(`Current Image Index: ${currentImageIndex}`);
console.log(`All Images Completed: ${allImagesCompleted}`);

// Update metadata for tracking
const imagesMetadata = $input.item.json.imagesMetadata || [];
imagesMetadata.push({
    index: currentImageIndex,
    sectionName: $input.item.json.sectionName || $node["Prepare Image Prompts"].json.imagePrompts[currentImageIndex].section_name,
    failed: $json.imageGenerationFailed || false
});

// Calculate next image index
let nextImageIndex = currentImageIndex + 1;
if (nextImageIndex >= totalImages) {
    nextImageIndex = totalImages - 1;
}

// Return comprehensive state for next iteration
return {
    contentId: $input.item.json.contentId,
    directories: $input.item.json.directories,
    niche: $input.item.json.niche,
    style: $input.item.json.style,
    imagePrompts: $node["Prepare Image Prompts"].json.imagePrompts,
    imageSettings: $input.item.json.imageSettings,
    totalImages: totalImages,
    completedImages: completedImages,
    runIndex: runIndex, // Include the run index in the output
    allImagesCompleted: allImagesCompleted,
    imagesMetadata: imagesMetadata,
    currentImageIndex: nextImageIndex
};

// Create completion file to signal visual generation completion
const directories = $node["Visual Generation Webhook"].json.body.directories;
const contentId = $node["Visual Generation Webhook"].json.body.contentId || $input.item.json.contentId;
const completionPath = `${directories.main}/visual_complete.json`;

// Use the metadata we collected to create a more complete report
// Get image paths using the same path pattern as used in the workflow
const totalImages = $input.item.json.totalImages || 0;
const imageFiles = [];

// Generate the image file paths
for (let i = 0; i < totalImages; i++) {
  const formattedIndex = i.toString().padStart(2, '0');
  imageFiles.push(`${directories.images}/image_${formattedIndex}.png`);
}

const completionData = {
  contentId: contentId,
  visualComplete: true,
  totalImages: totalImages,
  imageFiles: imageFiles,
  timestamp: new Date().toISOString()
};

// Convert to JSON string with proper formatting
const jsonString = JSON.stringify(completionData, null, 2);

// Convert to Buffer for binary data
const buffer = Buffer.from(jsonString, 'utf-8');

// Return with proper binary data format
return [
  {
    json: {
      ...($input.item.json || {}),
      completionPath: completionPath,
      completionData: completionData,
      visualComplete: true
    },
    binary: {
      data: {
        data: buffer,
        mimeType: 'application/json',
        fileName: 'visual_complete.json'
      }
    }
  }
];

// Log generation summary
const contentId = $node["Visual Generation Webhook"].json.body.contentId || $input.item.json.contentId;
const totalImages = $input.item.json.totalImages || 0;
const completedImages = $input.item.json.completedImages || 0;

// Determine how many images failed if that data is available
let failedImages = 0;
if ($input.item.json.imagesMetadata) {
  failedImages = $input.item.json.imagesMetadata.filter(img => img.failed).length;
} else {
  // Estimate failures based on completed vs total
  failedImages = Math.max(0, totalImages - completedImages);
}

const summary = {
  contentId: contentId,
  totalImagesRequested: totalImages,
  imagesSuccessfullyGenerated: completedImages - failedImages,
  imagesFailed: failedImages,
  generationCompleted: new Date().toISOString()
};

// Create formatted log message
const logMessage = `
VISUAL GENERATION SUMMARY
------------------------
Content ID: ${summary.contentId}
Total Images: ${summary.totalImagesRequested}
Successful: ${summary.imagesSuccessfullyGenerated}
Failed: ${summary.imagesFailed}
Completed at: ${summary.generationCompleted}
`;

console.log(logMessage);

return {
  ...summary,
  ...($input.item.json)
};

// Function Node to properly check Stable Diffusion API response
const responseData = $input.item.json;
let success = false;

// Log the response structure for debugging
console.log("Response structure:", JSON.stringify(responseData, null, 2).substring(0, 500) + "...");

// Check if the images array exists and has at least one item
if (responseData.body && 
    responseData.body.images && 
    Array.isArray(responseData.body.images) && 
    responseData.body.images.length > 0) {
  success = true;
  console.log("Success: Found images array with data");
} 
// Fallback check for different response structures
else if (responseData.images && 
         Array.isArray(responseData.images) && 
         responseData.images.length > 0) {
  success = true;  
  console.log("Success: Found top-level images array");
}
// Check for error indicators
else if (responseData.error || 
        (responseData.body && responseData.body.error)) {
  success = false;
  console.log("Error detected in response");
}
// If nothing else helps, check if we have any data at all
else {
  console.log("No clear success or failure indicators found");
  // Default to success if we have any kind of response data
  success = Object.keys(responseData).length > 0;
}

// Return the original data plus our success flag
return {
  ...responseData,
  imageGenerationSuccess: success
};
